codeExamples.basicUsage:
from rfs import Result, Success, Failure

def divide(a: int, b: int) -> Result[float, str]:
    if b == 0:
        return Failure('Division by zero')
    return Success(a / b)

result = divide(10, 2)
if result.is_success:
    print(f'Result: {result.unwrap()}')

codeExamples.hexagonalArchitecture:
from rfs.core.annotations import Port, Adapter, UseCase, Controller

@Port(name='user_repository')
class UserRepository(ABC):
    @abstractmethod
    async def find_by_id(self, user_id: str) -> Result[User, str]:
        pass

@Adapter(port='user_repository')
class PostgresUserRepository(UserRepository):
    async def find_by_id(self, user_id: str) -> Result[User, str]:
        # Implementation
        pass

@UseCase(dependencies=['user_repository'])
class GetUserUseCase:
    def __init__(self, user_repository: UserRepository):
        self.repo = user_repository

codeExamples.securityAndAccessControl:
from rfs.security.access_control import RequiresRole, RequiresPermission, RequiresAuthentication
from rfs.security.validation_decorators import ValidateInput, RateLimited

@Controller(route='/api/admin')
class AdminController:
    @RequiresAuthentication()
    @RequiresRole(UserRole.ADMIN)
    @RateLimited(max_calls=10, period=timedelta(minutes=1))
    @ValidateInput({'action': {'type': 'string', 'required': True}})
    async def admin_action(self, request_data: dict) -> dict:
        return await self.admin_service.execute_action(request_data)

